import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUser } from '../context/UserContext';

const Login = () => {
  const [isRegisterForm, setIsRegisterForm] = useState(false); // Флаг для переключения между формами
  const [phoneNumber, setPhoneNumber] = useState(''); // Поле для номера телефона
  const [email, setEmail] = useState(''); // Поле для электронной почты
  const [password, setPassword] = useState(''); // Поле для пароля
  const { login, register } = useUser(); // Предполагаем наличие функции register в контексте UserContext
  const navigate = useNavigate();
{/*}
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!isRegisterForm) {
      // Обработка формы авторизации
      const userData = { name: 'Иван Иванов', email };
      login(userData);
      navigate('/profile');
    } else {
      // Обработка формы регистрации
      const userData = { phoneNumber, email, password };
      register(userData);
      navigate('/login'); // Переход обратно к форме авторизации после успешной регистрации
    }
  };
*/}
  const handleSubmit = async (event) => {
    event.preventDefault();
    
    try {
        // Отправка данных на сервер через fetch API
        const response = await fetch('https://registration-fastapi.onrender.com/registration', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({phone_number, email, password }) 
        });
        
        if (!response.ok) {
            throw new Error(await response.text());
        }

        alert('Вы успешно зарегистрированы!');
       } catch (error) {
        setErrorMessage(error.message);
    }
};

  return (
    <div className="min-h-screen flex justify-center items-center bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold text-center mb-6">{isRegisterForm ? 'Регистрация' : 'Авторизация'}</h2>
        <form onSubmit={handleSubmit}>
          {!isRegisterForm && (
            <>
              <div className="mb-4">
                <label htmlFor="email" className="block text-sm font-semibold mb-2">Email</label>
                <input
                  id="email"
                  type="email"
                  className="w-full p-3 border border-gray-300 rounded-md"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <div className="mb-6">
                <label htmlFor="password" className="block text-sm font-semibold mb-2">Пароль</label>
                <input
                  id="password"
                  type="password"
                  className="w-full p-3 border border-gray-300 rounded-md"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
            </>
          )}
          {isRegisterForm && (
            <>
              <div className="mb-4">
                <label htmlFor="phoneNumber" className="block text-sm font-semibold mb-2">Номер телефона</label>
                <input
                  id="phoneNumber"
                  type="tel"
                  className="w-full p-3 border border-gray-300 rounded-md"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  required
                />
              </div>
              <div className="mb-4">
                <label htmlFor="email" className="block text-sm font-semibold mb-2">Электронная почта</label>
                <input
                  id="email"
                  type="email"
                  className="w-full p-3 border border-gray-300 rounded-md"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <div className="mb-6">
                <label htmlFor="password" className="block text-sm font-semibold mb-2">Пароль</label>
                <input
                  id="password"
                  type="password"
                  className="w-full p-3 border border-gray-300 rounded-md"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
            </>
          )}
          <button
            type="submit"
            className="w-full py-3 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 transition"
          >
            {isRegisterForm ? 'Зарегистрироваться' : 'Войти'}
          </button>
          <p className="mt-4 text-center">
            {isRegisterForm ? 'Уже есть аккаунт?' : 'Нет аккаунта?'}{' '}
            <span
              role="link"
              tabIndex={0}
              onClick={() => setIsRegisterForm(!isRegisterForm)}
              className="cursor-pointer underline text-blue-600"
            >
              {isRegisterForm ? 'Войдите' : 'Зарегистрируйтесь'}
            </span>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Login;
