import React, { useState } from 'react';
import { useUser } from '../context/UserContext';
import { Link } from 'react-router-dom';
import { FaUserCircle } from 'react-icons/fa';
import Form from './Form'; // Импортируем компонент формы

const Profile = () => {
  const { user, logout, updateUser } = useUser();
  const [profileData, setProfileData] = useState(user || {});
  const [isEditing, setIsEditing] = useState(false); // Toggle visibility for profile form
  const [isFormVisible, setIsFormVisible] = useState(false); // Toggle visibility for recommendation form
  const [classificationData, setClassificationData] = useState({
    gender: '',
    hostel: '',
    gpa: 4,
    priority: 0,
    exams_points: 0,
    bonus_points: 2,
    education: '',
    study_form: '',
    reception_form: '',
    speciality: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData({ ...profileData, [name]: value });
  };

  const saveChanges = () => {
    updateUser(profileData);
    alert('Данные успешно сохранены!');
  };

  const toggleEditing = () => {
    setIsEditing(!isEditing);
  };


  const toggleFormVisibility = () => {
    setIsFormVisible(!isFormVisible); // Toggle visibility of the recommendation form
  };

  if (!user) {
    return (
      <div className="min-h-screen flex justify-center items-center bg-blue-100">
        <div className="bg-white p-8 rounded-lg shadow-lg">
          <p>Вы не авторизованы. Пожалуйста, войдите.</p>
          <Link to="/login" className="mt-4 text-blue-500">Войти</Link>
        </div>
      </div>
    );
  }

  return (
    <div
      className="min-h-screen flex flex-col md:flex-row bg-cover bg-center"
      style={{
        backgroundImage: "url('https://sun9-54.userapi.com/impf/wqkRXyU9ntJHyWoDl3YhZKx8Q0H9oscYe13odA/c5jpwGXROr4.jpg?size=1920x768&quality=95&crop=170,0,1750,699&sign=c4acd10a294944e2b0816fbfc4e8dfb8&type=cover_group')"
      }}
      
    >
      {/* Левый блок: Навигация */}
      <div className="hidden md:block w-70 h-screen fixed left-0 top-0 bg-white border-r shadow-md overflow-x-hidden z-50">
        <ul className="list-none mt-12">
          <li className="mb-4">
            <button onClick={toggleFormVisibility} className="px-4 py-2 block text-lg hover:bg-gray-200">Рекомендательная система</button>
          </li>
          <li className="mb-4">
            <button
              onClick={toggleEditing}
              className="px-4 py-2 block text-lg hover:bg-gray-200"
            >
              Мои данные
            </button>
          </li>
          <li className="mb-4">
            <button onClick={() => logout()} className="px-4 py-2 block text-lg hover:bg-gray-200">Выход</button>
          </li>
        </ul>
      </div>

      {/* Центральный блок */}
      <div className="flex-1 flex items-center justify-center">
        {isFormVisible ? (
          <div className="bg-white p-8 rounded-lg shadow-lg">
            <h2 className="text-xl font-bold mb-4">Рекомендательная система</h2>
            <div className="w-full md:w-100 flex flex-col gap-6 p-4">
        <div>
          {isFormVisible ? (
            <div className="bg-white p-8 rounded-lg shadow-lg">
              <Form />
            </div>
          ) : null}
          
        </div>
        
      </div>
          </div>
        ) : (
          <div
            
            className="bg-white p-8 rounded-lg shadow-lg cursor-pointer text-center"
          >
            <h2 className="text-xl font-bold mb-4">Добро пожаловать!</h2>
            <p>Нажмите, чтобы воспользоваться рекомендательной системой.</p>
          </div>
        )}
      </div>
      
      {/* Мои данные */}
      {isEditing && (
        <div className="absolute top-0 left-0 w-full h-full bg-gray-900 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-8 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">Редактирование профиля</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-bold mb-1">Имя</label>
                <input
                  type="text"
                  name="name"
                  value={profileData.name}
                  onChange={handleInputChange}
                  className="border rounded p-2 w-full"
                />
              </div>
              <div>
                <label className="block text-sm font-bold mb-1">Email</label>
                <input
                  type="email"
                  name="email"
                  value={profileData.email}
                  onChange={handleInputChange}
                  className="border rounded p-2 w-full"
                />
              </div>
              <div className="flex justify-between mt-4">
                <button
                  onClick={saveChanges}
                  className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                >
                  Сохранить
                </button>
                <button
                  onClick={toggleEditing}
                  className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
                >
                  Отмена
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Profile;
