import React, { useState } from 'react';

const ClassifierForm = () => {
    const [formData, setFormData] = useState({
        top_n: '',
    user: {
        age: '',
        gender: '',
        sport: '',
        foreign: '',
        gpa: '',
        points: '',
        bonus_points: 0,
        exams: [],
        reception_form: '',
        priority: '',
        education: '',
        study_form: '',
        direction: ''
    },
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            user: {
                ...prevState.user,
                [name]: value
            }
        }));
    };

    const handleRangeChange = (e) => {
        const { value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            user: {
                ...prevState.user,
                total_points: value
            }
        }));
    };

    const handleExamClick = (exam) => {
        let updatedExams = [...formData.user.exams];
        if (updatedExams.includes(exam)) {
            updatedExams = updatedExams.filter(e => e !== exam);
        } else {
            updatedExams.push(exam);
        }
        setFormData(prevState => ({
            ...prevState,
            user: {
                ...prevState.user,
                exams: updatedExams
            }
        }));
    };

    const handleEducationChange = (e) => {
        const { value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            education: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('/api/classifier', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });

            const result = await response.json();
            alert(`Результат классификации: ${JSON.stringify(result)}`);
        } catch (error) {
            console.error('Ошибка:', error);
            alert('Произошла ошибка при отправке данных.');
        }
    };

    return (
        <div className="container mx-auto p-6">
            <form onSubmit={handleSubmit} className="bg-white p-8 shadow-xl rounded-lg w-full max-w-xl">
                

                <fieldset className="space-y-4 mb-6">
                    <label className="block text-sm font-semibold">Пол:
                        <select
                            value={formData.user.gender}
                            name="gender"
                            onChange={handleChange}
                            className="w-full p-2 mt-2 border rounded-lg focus:ring-2 focus:ring-purple-500"
                            required
                        >
                            <option value="">Выберите пол</option>
                            <option value="М">Мужской</option>
                            <option value="Ж">Женский</option>
                        </select>
                    </label>

                    <label className="block text-sm font-semibold">Возраст:
                        <input
                            type="number"
                            value={formData.user.age}
                            name="age"
                            onChange={handleChange}
                            className="w-full p-2 mt-2 border rounded-lg focus:ring-2 focus:ring-purple-500"
                            required
                        />
                    </label>

                    <label className="block text-sm font-semibold">Вид спорта:
                        <input
                            type="text"
                            value={formData.user.sport}
                            name="sport"
                            onChange={handleChange}
                            className="w-full p-2 mt-2 border rounded-lg focus:ring-2 focus:ring-purple-500"
                            required
                        />
                    </label>

                    <label className="block text-sm font-semibold">Средний балл (GPA):
                        <input
                            type="number"
                            step="0.01"
                            value={formData.user.gpa}
                            name="gpa"
                            onChange={handleChange}
                            className="w-full p-2 mt-2 border rounded-lg focus:ring-2 focus:ring-purple-500"
                            required
                        />
                    </label>

                    <label className="block text-sm font-semibold">Общее количество баллов:
                        <input
                            type="range"
                            min="0"
                            max="400"
                            step="1"
                            value={formData.user.total_points}
                            onChange={handleRangeChange}
                            className="w-full mt-2"
                        />
                        <span className="text-sm">{formData.user.total_points} баллов</span>
                    </label>

                    <label className="block text-sm font-semibold">Дополнительные баллы:
                        <input
                            type="number"
                            value={formData.user.bonus_points}
                            name="bonus_points"
                            onChange={handleChange}
                            className="w-full p-2 mt-2 border rounded-lg focus:ring-2 focus:ring-purple-500"
                            required
                        />
                    </label>

                    <label className="block text-sm font-semibold">Выберите экзамены:
                        <div className="grid grid-cols-2 gap-2 mt-2">
                            {egeExams.map((exam) => (
                                <div
                                    key={exam}
                                    className={`p-2 border rounded-lg cursor-pointer ${
                                        formData.user.exams.includes(exam) ? 'bg-purple-500 text-white' : 'bg-gray-200'
                                    }`}
                                    onClick={() => handleExamClick(exam)}
                                >
                                    {exam}
                                </div>
                            ))}
                        </div>
                    </label>

                    <label className="block text-sm font-semibold">Вид образования:
                        <select
                            value={formData.education}
                            onChange={handleEducationChange}
                            className="w-full p-2 mt-2 border rounded-lg focus:ring-2 focus:ring-purple-500"
                            required
                        >
                            <option value="">Выберите вид образования</option>
                            <option value="бакалавр">Бакалавр</option>
                            <option value="магистр">Магистр</option>
                            <option value="специалист">Специалист</option>
                        </select>
                    </label>
                </fieldset>

                <button type="submit" className="block w-full py-2 mt-4 bg-purple-600 hover:bg-purple-700 text-white font-bold rounded-lg">
                    Отправить
                </button>
            </form>
        </div>
    );
};

export default ClassifierForm;
